import React, { Component, Fragment } from 'react';
import { Helmet } from "react-helmet";
import quastions from '../../quastions.json';
import isEmpty from '../../utils/is-empty'
class Play extends Component {
    constructor (props) {
        super (props);
        this.state = {
            quastions: [],
            currentQuastion: {},
            nextQuastion: {},
            previousQuastion: {},
            answer: '',
            numberOfQuastions: 0,
            numberOfAnswerQuastions: 0,
            currentQuastionIndex: 0,
            score: 0,
            correctAnswers:0,
            wrongAnswers:0,
            hints: 5,
            fiftyFifty: 2, 
            usedFiftyFifty: false,
            time: {}
        };
    }

    componentDidMount () {
        const {quastions, currentQuastion, nextQuastion, previousQuastion} = this.state
        this.displayQuastions(quastions, currentQuastion, nextQuastion, previousQuastion);
    }

    displayQuastions = (quastions = this.state.quastions, currentQuastion, nextQuastion, previousQuastion) => {
        let { currentQuastionIndex} = this.state;
        if (!isEmpty(this.state.quastions)) {
            quastions = this.state.quastions;
            currentQuastion = quastions[currentQuastionIndex];
            nextQuastion = quastions[currentQuastionIndex + 1];
            previousQuastion = quastions[currentQuastionIndex -1];
            const answer = currentQuastion.answer;
            this.setState ({
                currentQuastion ,
                nextQuastion,
                previousQuastion,
                answer
            });
        }
    };


    increaseCount = () => {
        this.setState({
            counter: 5
        });
    };

    render() {
        //console.log(quastions);
        const { currentQuastion } = this.state
        return (
            <Fragment>
                <Helmet><title>Programming assignment - Quiz Page</title></Helmet>
                <div className="quastions">
                    <div className="lifeline-container">
                        <p>
                            <span className="mdi mdi-set-center mdi-24px lifeline-icon"></span><span className="lifeline">1</span>
                        </p>
                        <p>
                            <span className="mdi mdi-lightbulb mdi-24px lifeline-icon"></span><span className="lifeline">5</span>
                        </p>
                    </div>
                    <div className="timer-container">
                        <p>
                            <span className="left">1/10</span>
                            <span className="right"><span className="mdi mdi-clock-outline mdi-24px"></span>1:30</span>
                        </p>
                    </div>
                    <h5>Which of the following is not an organ in the human body?</h5>
                    <div className="option-container"> 
                        <p className="option">Pancreas</p>
                        <p className="option" >Ulna</p>
                    </div>
                    <div className="option-container"> 
                        <p className="option">Gallbladder</p>
                        <p className="option">Eye</p>
                    </div>
                    <div className="button-container">
                        <button>Previous</button>
                        <button>Next</button>
                        <button>Quit</button>
                    </div>
                </div>
            </Fragment>
        );
    }
}

export default Play;
