import React, { Component, Fragment } from 'react';
import { Helmet } from "react-helmet";
import questions from '../../questions.json';
import isEmpty from '../../utils/is-empty'
class Play extends Component {
    constructor (props) {
        super (props);
        this.state = {
            questions,
            currentQuestion: {},
            nextQuestion: {},
            previousQuestion: {},
            answer: '',
            numberOfQuestions: 0,
            numberOfAnswerQuestions: 0,
            currentQuestionIndex: 0,
            score: 0,
            correctAnswers:0,
            wrongAnswers:0,
            hints: 5,
            fiftyFifty: 2, 
            usedFiftyFifty: false,
            time: {}
        };
    }

    componentDidMount () {
        const {questions, currentQuestion, nextQuestion, previousQuestion} = this.state
        this.displayQuastions(questions, currentQuestion, nextQuestion, previousQuestion);
    }

    displayQuestions = (questions = this.state.questions, currentQuestion, nextQuestion, previousQuestion) => {
        let { currentQuestionIndex} = this.state;
        if (!isEmpty(this.state.questions)) {
            questions = this.state.questions;
            currentQuestion = questions[currentQuestionIndex];
            nextQuestion = questions[currentQuestionIndex + 1];
            previousQuestion = questions[currentQuestionIndex -1];
            const answer = currentQuestion.answer;
            this.setState ({
                currentQuestion ,
                nextQuestion,
                previousQuestion,
                answer
            });
        }
    };


    increaseCount = () => {
        this.setState({
            counter: 5
        });
    };

    render() {
        //console.log(questions);
        const { currentQuastion } = this.state

        return (
            <Fragment>
                <Helmet><title>Programming assignment - Quiz Page</title></Helmet>
                <div className="questions">
                    <div className="lifeline-container">
                        <p>
                            <span className="mdi mdi-set-center mdi-24px lifeline-icon"></span><span className="lifeline">1</span>
                        </p>
                        <p>
                            <span className="mdi mdi-lightbulb mdi-24px lifeline-icon"></span><span className="lifeline">5</span>
                        </p>
                    </div>
                    <div className="timer-container">
                        <p>
                            <span className="left">1/10</span>
                            <span className="right"><span className="mdi mdi-clock-outline mdi-24px"></span>1:30</span>
                        </p>
                    </div>
                    <h5>{currentQuastion.question}</h5>
                    <div className="option-container"> 
                        <p className="option">{currentQuastion.optionA}</p>
                        <p className="option" >{currentQuastion.optionB}</p>
                    </div>
                    <div className="option-container"> 
                        <p className="option">{currentQuastion.optionC}</p>
                        <p className="option">{currentQuastion.optionD}</p>
                    </div>
                    <div className="button-container">
                        <button>Previous</button>
                        <button>Next</button>
                        <button>Quit</button>
                    </div>
                </div>
            </Fragment>
        );
    }
}

export default Play;
