import React, { Component } from 'react';

class Play extends React.Component {
    constructor (props) {
        this.state = {
            counter: 0
        };
    }
    render() {
        return (
            <>
            <p>counter: {this.state.counter}</p>
            <button onClick={this.increaseCount}>Click me</button>
            </>
        );
    }
}

export default Play;
