import React, { Component } from 'react';

class Play extends Component {
    constructor (props) {
        super (props);
        this.state = {
            counter: 0
        };
    }

    increaseCount = () => {
        this.setState({
            counter: 5
        });
    };

    render() {
        return (
            <>
            <div>
            <p>Counter: {this.state.counter}</p>
            <button onClick={this.increaseCount}>Click me</button>
            </div>
            </>
        );
    }
}

export default Play;
